const slider=()=>{const t=document.querySelectorAll(".slide");for(slide of t)slide.addEventListener("click",e=>{t.forEach(e=>{e.classList.remove("active")}),e.target.classList.add("active")})},popUp=(slider(),()=>{for(btn of document.querySelectorAll(".slide__button"))btn.addEventListener("click",e=>{e.preventDefault();const t=document.querySelector(".popUp");t.classList.add("popUp--active")});const e=document.querySelector(".popUp__closeButton");e.addEventListener("click",e=>{e.preventDefault();const t=document.querySelector(".popUp");t.classList.remove("popUp--active")})});popUp();